import React from 'react';
import {connect} from "react-redux";
import {SUPRIMER_TITULAIR} from "../stores/actions";
import soccer from '../styles/soccer.svg';
import Box from '@material-ui/core/Box';
import Draggable from 'react-draggable'; // The default
import Avatar from '@material-ui/core/Avatar';
import Badge from '@material-ui/core/Badge';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';


class Titulares extends React.Component  {
    constructor(props){
        super(props)
        this.state = {
            activeDrags: 0,
            deltaPosition: {
                x: 0, y: 0
            },
        };
    }

    handleDrag = (e, ui,id) => {
        const {x, y} = this.state.deltaPosition;
        console.log('e',e);
        console.log('ui',ui);
        console.log('k',id);
        this.setState({
            deltaPosition: {
                [x+id]: x[id] + ui.deltaX,
                [y+id]: y[id] + ui.deltaY,
            }
        });
    };
    componentDidUpdate(){
        console.log('state',this.state)
    }

    onStart = () => {

        this.setState({activeDrags: ++this.state.activeDrags});
    };

    onStop = () => {
        this.setState({activeDrags: --this.state.activeDrags});
    };


    render() {
        return(
            <section>
                <h2>Titulaires</h2>
                <img src={soccer} alt={"Soccer"}/>
                <div  style={{    flexDirection: 'row', display: 'flex'}}
                      className="cancha">
                    {
                        this.props.titulaires.map((j) => (
                            <article style={{display: 'flex',alignItems: 'center',flexDirection: 'column'}}
                                     key={j.id}
                                     className="titulaires">
                                <Draggable onDrag={(e,ui)=>this.handleDrag(e,ui,j.id)} onStart={this.onStart} onStop={this.onStop}>
                                    <Box style={{justifyContent: 'center',
                                        alignItems: 'center',
                                        display: 'flex',
                                        flexDirection: 'column'}} m={1}>
                                        <p style={{margin:0}}>{j.nombre}</p>
                                        <Badge
                                            overlap="circle"
                                            anchorOrigin={{vertical: 'bottom', horizontal: 'right',}}
                                            badgeContent={
                                                <HighlightOffIcon
                                                    onClick={()=>{this.props.removeTitulair(j)}}
                                                    style={{color:'red'}}
                                                />
                                            }>
                                            <Avatar src={j.foto} alt={j.nombre} />
                                        </Badge>
                                    </Box>
                                </Draggable>
                            </article>
                        ))
                    }
                </div>
            </section>
        )
    }
}
const mapStateToProps = state => ({
    titulaires: state.titulaires
});
const mapDispatchToProps = dispatch => ({
    removeTitulair(joueur){
        dispatch({
            type: SUPRIMER_TITULAIR,
            joueur
        })
    }
});

export  default connect(mapStateToProps,mapDispatchToProps)(Titulares);
